# combien de handle dans l'array ?

def compter (objet)
    puts "\n\nLe nombre de handle de cet array est : #{objet.length}"
end

# quel est le plus petit handle

def plus_petit (objet3)
    puts "\n\n\nLe handle le plus petit de cette liste est : #{list_mail.min_by(&:length)}"
end

# nombre de handle contenant 5 caractères (sans le @)

def nb_5(objet4)
    number = 0
    objet4.each do |i|
        if i.length == 6
            number += 1
        end
    end
    puts "\n\n\nLe nombre de handles de 5 caractères est = #{number}"
end

# nombre de handle commençant par une MAJ

def majus(objet5)
    number = 0
    objet5.each do |i|
     
        if i[1] =~ /[A-Z]/
            number += 1
        end
    end
    puts "\n\n\nLe nombre de handles commençants avec une majuscule est = #{number}"
end

# ordre alphabetique

def alpha_order (objet6)
    order = objet6.sort_by!{|i| i.downcase}
    puts "\n\n\nVoici l'ordre alphabétique des handles :\n"
    puts "\n",order
end

# ordre croissant

def size_order (objet7)
    size = objet7.sort_by{|i| i.length}
    puts "\n\n\n==> Voici l'ordre croissant des handles : \n"
    puts "\n",size
end

# position handle

def position(objet8)
    nom = objet8.find_index("@epenser")
    puts "\n\n\n==> Position de @epenser dans l'array : "
    puts "\n@epenser se trouve à la position #{nom} du handle"
end

# Voici la répartition des handle par taille

def liste_taille(objet9)
    
    liste_objet9 = objet9.sort_by!{|n| n.length}
    puts liste_objet9
end

def perform
compter(list_mail)
plus_petit(list_mail)
nb_5(list_mail)
majus(list_mail)
alpha_order(list_mail)
size_order(list_mail)
position(list_mail)

end

perform



